(21114, 17986, 'FACTEURMONITORING', 'http://files.spip.org/spip-zone/facteurmonitoring.png', '001.001.002', '', '[3.0.19;3.1.*]', '3.0,3.1', '<multi>\n[fr]Vérifie si le facteur envoie bien ses emails\n</multi>', 'a:1:{i:1;a:3:{s:3:\"nom\";s:9:\"erational\";s:3:\"url\";s:24:\"http://www.erational.org\";s:4:\"mail\";s:0:\"\";}}', '', 'a:1:{i:1;a:2:{s:3:\"nom\";s:7:\"GNU/GPL\";s:3:\"url\";s:0:\"\";}}', '', 'https://contrib.spip.net/4695', '', '', 'dev', 2, 'a:3:{s:9:\"necessite\";a:1:{i:0;a:2:{s:7:\"saisies\";a:2:{s:3:\"nom\";s:7:\"saisies\";s:13:\"compatibilite\";s:8:\"[2.0.0;]\";}s:7:\"facteur\";a:2:{s:3:\"nom\";s:7:\"facteur\";s:13:\"compatibilite\";s:8:\"[3.4.7;]\";}}}s:9:\"librairie\";a:0:{}s:7:\"utilise\";a:0:{}}', '', '2017-04-02 18:04:12', '2017-04-02 18:04:12', 1, 'facteurmonitoring.zip', 15946, '2017-04-02 18:04:36', '_plugins_/facteurmonitoring', 'a:1:{s:17:\"facteurmonitoring\";a:2:{s:9:\"reference\";s:2:\"fr\";s:12:\"gestionnaire\";s:0:\"\";}}', 'non', 'non', 0, '', 'non', 'non', 'non', '', '5a53890ea8cf4e961896ab70b872d21e'),
(21115, 17987, 'FAQ', 'http://files.spip.org/spip-zone/faq.png', '003.000.010', '', '[3.0.0;3.1.*]', '3.0,3.1', '<multi>\n[fr]Ajoute de nouveaux raccourcis typographiques permettant de décrire de manière simple des <abbr title=\"Foire Aux Questions\">FAQ</abbr> dans un contenu SPIP.\n\n	Les raccourcis doivent être utilisés à l\'intérieur de la balise <code><faq></faq></code>. Une configuration permet d\'ajouter des ancres, de rendre la FAQ dépliable, de charger des styles par défaut...\n	La structure HTML produite est, par défaut, toujours basée sur une liste de définitions.\n</multi>', 'a:2:{i:1;a:3:{s:3:\"nom\";s:5:\"Tetue\";s:3:\"url\";s:21:\"http://spip.tetue.net\";s:4:\"mail\";s:0:\"\";}i:2;a:3:{s:3:\"nom\";s:14:\"Eric Lupinacci\";s:3:\"url\";s:23:\"http://blog.smellup.net\";s:4:\"mail\";s:0:\"\";}}', '', 'a:1:{i:1;a:2:{s:3:\"nom\";s:5:\"GPL 3\";s:3:\"url\";s:40:\"http://www.gnu.org/licenses/gpl-3.0.html\";}}', 'a:1:{i:1;s:9:\"2009-2015\";}', 'https://contrib.spip.net/Definitions-et-FAQ-version-3', '', '', 'stable', 4, 'a:3:{s:9:\"necessite\";a:1:{i:0;a:1:{s:2:\"tw\";a:2:{s:3:\"nom\";s:2:\"tw\";s:13:\"compatibilite\";s:9:\"[0.8.11;[\";}}}s:9:\"librairie\";a:0:{}s:7:\"utilise\";a:0:{}}', '', '2017-04-02 18:04:12', '2017-04-02 18:04:12', 1, 'faq.zip', 64848, '2017-04-02 18:04:36', '_plugins_/faq/trunk', 'a:1:{s:3:\"faq\";a:2:{s:9:\"reference\";s:2:\"fr\";s:12:\"gestionnaire\";s:0:\"\";}}', 'non', 'non', 0, '', 'non', 'non', 'non', '', '3bd75977376a2eec8029dadc58a496df'),
