DROP TABLE IF EXISTS grr_j_mailuser_room;
CREATE TABLE grr_j_mailuser_room (login varchar(40) NOT NULL default '', id_room int(11) NOT NULL default '0', PRIMARY KEY  (login,id_room) );
DROP TABLE IF EXISTS grr_j_user_area;
CREATE TABLE grr_j_user_area (login varchar(40) NOT NULL default '', id_area int(11) NOT NULL default '0', PRIMARY KEY  (login,id_area) );
DROP TABLE IF EXISTS grr_j_user_room;
CREATE TABLE grr_j_user_room (login varchar(40) NOT NULL default '', id_room int(11) NOT NULL default '0', PRIMARY KEY  (login,id_room) );
DROP TABLE IF EXISTS grr_j_useradmin_area;
CREATE TABLE grr_j_useradmin_area (login varchar(40) NOT NULL default '', id_area int(11) NOT NULL default '0', PRIMARY KEY  (login,id_area) );
DROP TABLE IF EXISTS grr_abt;
CREATE TABLE grr_abt (id int(10) NOT NULL auto_increment, abt_name varchar(20) NOT NULL, order_display smallint(6) NOT NULL default '0', PRIMARY KEY  (id));
DROP TABLE IF EXISTS grr_log;
CREATE TABLE grr_log (LOGIN varchar(40) NOT NULL default '', START datetime NOT NULL default '0000-00-00 00:00:00', SESSION_ID varchar(64) NOT NULL default '', REMOTE_ADDR varchar(60) NOT NULL default '', USER_AGENT varchar(255) NOT NULL default '', REFERER varchar(255) NOT NULL default '', AUTOCLOSE enum('0','1') NOT NULL default '0', END datetime NOT NULL default '0000-00-00 00:00:00', PRIMARY KEY  (SESSION_ID,`START`));
DROP TABLE IF EXISTS grr_annonces;
CREATE TABLE grr_annonces( id int(11) NOT NULL auto_increment, login varchar(40) NOT NULL, nom varchar(30) NOT NULL,  prenom varchar(30) NOT NULL, dispos text NOT NULL, PRIMARY KEY  (id));
DROP TABLE IF EXISTS grr_area;
CREATE TABLE grr_area (id int(11) NOT NULL auto_increment,area_name varchar(30) NOT NULL default '',access char(1) NOT NULL default '', order_display smallint(6) NOT NULL default '0', ip_adr varchar(15) NOT NULL default '', morningstarts_area smallint(6) NOT NULL default '0', minute_morningstarts_area smallint(6) NOT NULL default '0',eveningends_area smallint(6) NOT NULL default '0', resolution_area smallint(6) NOT NULL default '0', eveningends_minutes_area smallint(6) NOT NULL default '0', weekstarts_area smallint(6) NOT NULL default '0', twentyfourhour_format_area smallint(6) NOT NULL default '0', calendar_default_values char(1) NOT NULL default 'y', enable_periods char(1) NOT NULL default 'n',display_days varchar(7) NOT NULL default 'yyyyyyy', group_id smallint(2) NOT NULL default '0',  duree_par_defaut_reservation_area smallint(2) NOT NULL default '0', PRIMARY KEY  (id));
DROP TABLE IF EXISTS grr_calendar;
CREATE TABLE grr_calendar ( DAY int(11) NOT NULL default '0');
DROP TABLE IF EXISTS grr_entry;
CREATE TABLE grr_entry ( id int(11) NOT NULL auto_increment, start_time int(11) NOT NULL default '0', end_time int(11) NOT NULL default '0', entry_type int(11) NOT NULL default '0', repeat_id int(11) NOT NULL default '0', room_id int(11) NOT NULL default '1', `timestamp` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, create_by varchar(100) NOT NULL default '', `name` varchar(80) NOT NULL default '', `type` char(1) NOT NULL default 'A', description text, statut_entry char(1) NOT NULL default '-', option_reservation int(11) NOT NULL default '0', overload_desc text, PRIMARY KEY  (id),  KEY idxStartTime (start_time),  KEY idxEndTime (end_time));
DROP TABLE IF EXISTS grr_repeat;
CREATE TABLE grr_repeat ( id int(11) NOT NULL auto_increment, start_time int(11) NOT NULL default '0', end_time int(11) NOT NULL default '0', rep_type int(11) NOT NULL default '0',  end_date int(11) NOT NULL default '0', rep_opt varchar(32) NOT NULL default '',  room_id int(11) NOT NULL default '1', `timestamp` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, create_by varchar(100) NOT NULL default '', `name` varchar(80) NOT NULL default '', `type` char(1) NOT NULL default 'E', description text, rep_num_weeks tinyint(4) default '0', overload_desc text, PRIMARY KEY  (id));
DROP TABLE IF EXISTS grr_room;
CREATE TABLE grr_room ( id int(11) NOT NULL auto_increment, area_id int(11) NOT NULL default '0', room_name varchar(60) NOT NULL default '', description varchar(60) NOT NULL default '', capacity int(11) NOT NULL default '0', max_booking smallint(6) NOT NULL default '-1',max_booking_week smallint(6) NOT NULL default '-1', statut_room char(1) NOT NULL default '1',  show_fic_room char(1) NOT NULL default 'n', show_comment CHAR(1) NOT NULL DEFAULT 'n', picture_room varchar(50) NOT NULL default '', comment_room text NOT NULL, delais_max_resa_room smallint(6) NOT NULL default '-1', delais_min_resa_room smallint(6) NOT NULL default '0', allow_action_in_past char(1) NOT NULL default 'n', dont_allow_modify char(1) NOT NULL default 'n', order_display smallint(6) NOT NULL default '0', delais_option_reservation smallint(6) NOT NULL default '0', type_affichage_reser smallint(6) NOT NULL default '0', PRIMARY KEY  (id)) AUTO_INCREMENT=1;
DROP TABLE IF EXISTS grr_utilisateurs;
CREATE TABLE grr_utilisateurs (login varchar(40) NOT NULL DEFAULT '', nom varchar(30) NOT NULL DEFAULT '', prenom varchar(30) NOT NULL DEFAULT '', `password` varchar(32) NOT NULL DEFAULT '',email varchar(100) NOT NULL DEFAULT '',statut varchar(30) NOT NULL DEFAULT '',etat varchar(20) NOT NULL DEFAULT '',default_area smallint(6) NOT NULL DEFAULT '0',default_room smallint(6) NOT NULL DEFAULT '0',default_style varchar(50) NOT NULL DEFAULT '',default_list_type varchar(50) NOT NULL DEFAULT '',default_language char(3) NOT NULL DEFAULT '',`source` varchar(10) NOT NULL DEFAULT 'local',datenais date NOT NULL,tel varchar(15) NOT NULL DEFAULT '',telport varchar(15) NOT NULL DEFAULT '',abt varchar(15) NOT NULL DEFAULT '',licence varchar(6) NOT NULL DEFAULT '',classement varchar(6) NOT NULL DEFAULT '',adresse varchar(35) NOT NULL DEFAULT '',`code` varchar(6) NOT NULL DEFAULT '',ville varchar(25) NOT NULL DEFAULT '',invite int(10) NOT NULL DEFAULT '0',champio varchar(10) NOT NULL DEFAULT 'inactif',group_id int(10) NOT NULL DEFAULT '1',badge varchar(20) NOT NULL DEFAULT '',solo varchar(10) NOT NULL DEFAULT 'inactif',`inviteactif` varchar(10) NOT NULL DEFAULT 'inactif', PRIMARY KEY  (login));
DROP TABLE IF EXISTS grr_setting;
CREATE TABLE grr_setting (  NAME varchar(32) NOT NULL default '',  VALUE text NOT NULL,  PRIMARY KEY  (NAME));
DROP TABLE IF EXISTS grr_area_periodes;
CREATE TABLE grr_area_periodes (id_area int(11) NOT NULL default '0', num_periode smallint(6) NOT NULL default '0', nom_periode varchar(100) NOT NULL default '', PRIMARY KEY  (`id_area`,`num_periode`));
DROP TABLE IF EXISTS grr_type_area;
CREATE TABLE grr_type_area (id int(11) NOT NULL auto_increment, type_name varchar(30) NOT NULL default '',order_display smallint(6) NOT NULL default '0',couleur smallint(6) NOT NULL default '0',type_letter char(2) NOT NULL default '', PRIMARY KEY  (id));
DROP TABLE IF EXISTS grr_j_type_area;
CREATE TABLE grr_j_type_area (id_type int(11) NOT NULL default '0', id_area int(11) NOT NULL default '0');
DROP TABLE IF EXISTS grr_group;
CREATE TABLE grr_group (id int(10) NOT NULL auto_increment, group_name varchar(30) NOT NULL default '', order_display smallint(6) NOT NULL default '0', couleur smallint(6) NOT NULL default '0', group_letter char(2) NOT NULL, PRIMARY KEY (id)) AUTO_INCREMENT=1 ;
DROP TABLE IF EXISTS grr_overload;
CREATE TABLE grr_overload ( id int(11) NOT NULL auto_increment, id_area int(11) NOT NULL default '0', fieldname varchar(25) NOT NULL default '', fieldtype varchar(25) NOT NULL default '', PRIMARY KEY  (id)) AUTO_INCREMENT=1 ;
DROP TABLE IF EXISTS grr_group_calendar;
CREATE TABLE grr_group_calendar( id int(11) NOT NULL auto_increment, start_time int(11) NOT NULL default '0', end_time int(11) NOT NULL default '0', entry_type int(11) NOT NULL default '0', repeat_id int(11) NOT NULL default '0', room_id int(11) NOT NULL default '1', `timestamp` timestamp NOT NULL default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP, create_by varchar(25) NOT NULL default '', `name` varchar(80) NOT NULL default '', group_id int(10) NOT NULL, description text, statut_entry char(1) NOT NULL default '-', option_reservation int(11) NOT NULL default '0', overload_desc text, PRIMARY KEY (id), KEY idxStartTime (start_time), KEY idxEndTime (end_time));
DROP TABLE IF EXISTS grr_group_repeat;
CREATE TABLE grr_group_repeat ( id int(11) NOT NULL auto_increment, start_time int(11) NOT NULL default '0', end_time int(11) NOT NULL default '0', rep_type int(11) NOT NULL default '0', end_date int(11) NOT NULL default '0', rep_opt varchar(32) NOT NULL default '', room_id int(11) NOT NULL default '1', `timestamp` timestamp NOT NULL default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP, create_by varchar(25) NOT NULL default '', `name` varchar(80) NOT NULL default '', group_id char(1) NOT NULL default 'E', description text, rep_num_weeks tinyint(4) default '0', overload_desc text, PRIMARY KEY  (id));
DROP TABLE IF EXISTS grr_site;
CREATE TABLE grr_site (id int(11) NOT NULL auto_increment, sitecode varchar(10) default NULL, sitename varchar(50) NOT NULL default '', adresse_ligne1 varchar(38) default NULL, adresse_ligne2 varchar(38) default NULL, adresse_ligne3 varchar(38) default NULL, cp varchar(5) default NULL, ville varchar(50) default NULL, pays varchar(50) default NULL, tel varchar(25) default NULL, fax varchar(25) default NULL, PRIMARY KEY (`id`));
DROP TABLE IF EXISTS grr_j_site_area;
CREATE TABLE grr_j_site_area ( id_site int(11) NOT NULL default '0', id_area int(11) NOT NULL default '0', PRIMARY KEY  (`id_site`,`id_area`));
DROP TABLE IF EXISTS grr_j_useradmin_site;
CREATE TABLE grr_j_useradmin_site (login varchar(40) NOT NULL default '', id_site int(11) NOT NULL default '0', PRIMARY KEY  (login,id_site) );
DROP TABLE IF EXISTS grr_categorie_compta;
CREATE TABLE grr_categorie_compta (id int(3) NOT NULL AUTO_INCREMENT,name varchar(30) NOT NULL, PRIMARY KEY (id));
DROP TABLE IF EXISTS grr_compta;
CREATE TABLE grr_compta(id int(11) NOT NULL AUTO_INCREMENT, login varchar(40) NOT NULL, statut varchar(15) NOT NULL, date date NOT NULL,description text NOT NULL,categorie int(3) NOT NULL,montant float NOT NULL,mode text NOT NULL,default_year int(4) NOT NULL,rap int(1) NOT NULL DEFAULT '0',timestamp timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (id));
DROP TABLE IF EXISTS grr_compte_tresorerie;
CREATE TABLE grr_compte_tresorerie(id int(11) NOT NULL AUTO_INCREMENT,name varchar(25) NOT NULL,solde float NOT NULL,courant int(1) NOT NULL DEFAULT '0', PRIMARY KEY (id));
CREATE TABLE IF NOT EXISTS sites (id int(10) NOT NULL AUTO_INCREMENT, name varchar(25) NOT NULL, email varchar(40) NOT NULL, PRIMARY KEY (id));
